import { InstrumentationConfig, InstrumentationBase, InstrumentationModuleDefinition } from '@opentelemetry/instrumentation';
import * as cohere from 'cohere-ai';

interface CohereInstrumentationConfig extends InstrumentationConfig {
    /**
     * Whether to log prompts, completions and embeddings on traces.
     * @default true
     */
    traceContent?: boolean;
    /**
     * A custom logger to log any exceptions that happen during span creation.
     */
    exceptionLogger?: (e: Error) => void;
}

declare class CohereInstrumentation extends InstrumentationBase {
    protected _config: CohereInstrumentationConfig;
    constructor(config?: CohereInstrumentationConfig);
    setConfig(config?: CohereInstrumentationConfig): void;
    protected init(): InstrumentationModuleDefinition;
    manuallyInstrument(module: typeof cohere): void;
    private wrap;
    private unwrap;
    private wrapperMethod;
    private _wrapPromise;
    private _startSpan;
    private _endSpan;
    private _setResponseSpanForRerank;
    private _setResponseSpanForChat;
    private _setResponseSpanForGenerate;
    private _getLlmRequestTypeByMethod;
    private _shouldSendPrompts;
}

export { CohereInstrumentation };
export type { CohereInstrumentationConfig };
